mapper.select:
select distinct re.* from sys_resource re join sys_role_resource sr on re.id = sr.resource_id join sys_role r on r.id = sr.role_id join sys_user_role ur on ur.role_id = r.id
and re.resource_type = #{resourceType}
and ur.user_id = #{userId}
order by re.seq

with temp ( [id], [pid],name,url,icon)
as
(
select re.id, re.pid,re.name,re.url,re.icon
from sys_resource re join sys_role_resource rr on re.id = rr.resource_id join sys_role r on r.id = rr.role_id
join sys_user_role ur on ur.role_id = r.id


        and re.resource_type = #{resourceType}


        and ur.user_id = #{userId}


        and re.pid = #{pid}


union all
select a.Id, a.pid,a.name,a.url,a.icon
from sys_resource a
inner join temp on a.pid = temp.[Id]


        and a.resource_type = #{resourceType}


)
select DISTINCT * from temp

mapper.insert:
insert into sys_resource (create_datetime, description, 
  icon, name, resource_type,
  seq, state, url, pid
  )
values (#{createDatetime,jdbcType=TIMESTAMP}, #{description,jdbcType=VARCHAR}, 
  #{icon,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{resourceType,jdbcType=INTEGER},
  #{seq,jdbcType=INTEGER}, #{state,jdbcType=INTEGER}, #{url,jdbcType=VARCHAR}, #{pid,jdbcType=BIGINT}
  )
